sys_script.script:
(function() {
  // Cancel any scheduled pause/resume events for this incident
  try {
    gs.eventQueueCancel('x_incident.pause_sla_start', current);
    gs.eventQueueCancel('x_incident.pause_sla_end', current);
  } catch (e) {}

  // Delete related task_sla records
  var ts = new GlideRecord('task_sla');
  ts.addQuery('task', current.getUniqueValue());
  ts.query();
  while (ts.next()) {
    ts.deleteRecord();
  }
})();
