sys_ui_page.client_script:
(function() {
  function submitPause() {
    var start = document.getElementById('pause_start').value;
    var end = document.getElementById('pause_end').value;
    if (!start || !end) {
      alert('Please provide both start and end date/times.');
      return;
    }
    var ga = new GlideAjax('IncHelperSlaPauseScheduler');
    ga.addParam('sysparm_name', 'schedule');
    ga.addParam('sysparm_incident_id', g_form ? g_form.getUniqueValue() : top && top.g_form ? top.g_form.getUniqueValue() : '');
    ga.addParam('sysparm_start', start);
    ga.addParam('sysparm_end', end);
    ga.getXMLAnswer(function(ans){
      if (ans === 'ok') {
        alert('SLA pause has been scheduled.');
        try { GlideDialogWindow.get().destroy(); } catch(e) {}
      } else {
        alert('Failed to schedule: ' + ans);
      }
    });
  }
  window._inc_pause_sla_submit = submitPause;
})();
